        data: {
          labels: labels,
          datasets: [{
            label: 'Reservations',
            data: data,
            backgroundColor: ['rgba(16, 185, 129, 0.9)', 'rgba(239, 68, 68, 0.9)', 'rgba(245, 158, 11, 0.9)', 'rgba(139, 92, 246, 0.9)', 'rgba(6, 182, 212, 0.9)', 'rgba(236, 72, 153, 0.9)'],
