import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.find.js";
import "core-js/modules/es.iterator.flat-map.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import { Chart, registerables } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';
Chart.register(...registerables, ChartDataLabels);
export default {
  name: 'AdminSummary',
  data() {
    return {
      barChart: null,
      pieChart: null,
      totalLocations: 0,
      totalRevenue: 0,
      occupancyRate: 0,
      peakLocation: '',
      utilizationTrend: '',
      systemPerformance: ''
    };
  },
  mounted() {
    this.fetchCostSummary();
    this.fetchSpotSummary();
  },
  methods: {
    async fetchCostSummary() {
      const token = localStorage.getItem("auth_token");
      try {
        const res = await fetch(`http://localhost:5000/admin/cost-summary`, {
          headers: {
            'Authorization': token
          }
        });
        const data = await res.json();
        if (Array.isArray(data) && data.length > 0) {
          const labels = data.map(item => item.location);
          const values = data.map(item => item.total_cost);

          // Calculate stats
          this.totalLocations = data.length;
          this.totalRevenue = values.reduce((sum, val) => sum + val, 0);

          // Find peak location
          const maxCost = Math.max(...values);
          this.peakLocation = data.find(item => item.total_cost === maxCost)?.location || 'N/A';
          this.renderBarChart(labels, values);
        }
      } catch (error) {
        console.error('Error fetching cost summary:', error);
        // Set fallback values
        this.totalLocations = 0;
        this.totalRevenue = 0;
        this.peakLocation = 'No data available';
      }
    },
    async fetchSpotSummary() {
      const token = localStorage.getItem("auth_token");
      try {
        const res = await fetch(`http://localhost:5000/admin/spot-summary`, {
          headers: {
            'Authorization': token
          }
        });
        const data = await res.json();
        if (Array.isArray(data) && data.length > 0) {
          const labels = data.map(item => item.location);
          const available = data.map(item => item.available);
          const occupied = data.map(item => item.occupied);

          // Calculate occupancy rate
          const totalAvailable = available.reduce((sum, val) => sum + val, 0);
          const totalOccupied = occupied.reduce((sum, val) => sum + val, 0);
          const totalSpots = totalAvailable + totalOccupied;
          this.occupancyRate = totalSpots > 0 ? Math.round(totalOccupied / totalSpots * 100) : 0;

          // Generate insights
          this.generateInsights(data);

          // Prepare chart data
          const chartData = {
            labels: labels.flatMap(loc => [`${loc} (Available)`, `${loc} (Occupied)`]),
            values: labels.flatMap((_, i) => [available[i], occupied[i]])
          };
          this.renderPieChart(chartData.labels, chartData.values);
        }
      } catch (error) {
        console.error('Error fetching spot summary:', error);
        // Set fallback values
        this.occupancyRate = 0;
        this.utilizationTrend = 'No data available';
        this.systemPerformance = 'Unable to assess';
      }
    },
    generateInsights(data) {
      // Utilization trend analysis
      if (this.occupancyRate > 80) {
        this.utilizationTrend = 'High demand - Consider expansion';
      } else if (this.occupancyRate > 50) {
        this.utilizationTrend = 'Optimal utilization levels';
      } else if (this.occupancyRate > 20) {
        this.utilizationTrend = 'Moderate usage patterns';
      } else {
        this.utilizationTrend = 'Low utilization - Review pricing';
      }

      // System performance assessment
      if (this.totalRevenue > 10000) {
        this.systemPerformance = 'Excellent revenue performance';
      } else if (this.totalRevenue > 5000) {
        this.systemPerformance = 'Good system performance';
      } else if (this.totalRevenue > 1000) {
        this.systemPerformance = 'Steady growth trajectory';
      } else {
        this.systemPerformance = 'Early stage metrics';
      }
    },
    renderBarChart(labels, data) {
      const ctx = document.getElementById('barChart');
      if (this.barChart) this.barChart.destroy();
      this.barChart = new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [{
            label: 'Total Revenue (₹)',
            data: data,
            backgroundColor: ['rgba(37, 99, 235, 0.8)', 'rgba(139, 92, 246, 0.8)', 'rgba(6, 182, 212, 0.8)', 'rgba(16, 185, 129, 0.8)', 'rgba(245, 158, 11, 0.8)', 'rgba(236, 72, 153, 0.8)'],
            borderColor: ['rgb(37, 99, 235)', 'rgb(139, 92, 246)', 'rgb(6, 182, 212)', 'rgb(16, 185, 129)', 'rgb(245, 158, 11)', 'rgb(236, 72, 153)'],
            borderWidth: 3,
            borderRadius: 12,
            borderSkipped: false
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            duration: 2000,
            easing: 'easeOutBounce'
          },
          plugins: {
            title: {
              display: true,
              text: 'Revenue by Location',
              font: {
                size: 20,
                weight: 'bold'
              },
              color: '#1f2937',
              padding: 20
            },
            legend: {
              display: false
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              grid: {
                color: 'rgba(107, 114, 128, 0.1)',
                lineWidth: 2
              },
              ticks: {
                color: '#6b7280',
                font: {
                  weight: '600',
                  size: 12
                },
                callback: function (value) {
                  return '₹' + value.toLocaleString();
                }
              }
            },
            x: {
              grid: {
                display: false
              },
              ticks: {
                color: '#6b7280',
                font: {
                  weight: '600',
                  size: 12
                }
              }
            }
          }
        }
      });
    },
    renderPieChart(labels, data) {
      const ctx = document.getElementById('pieChart');
      if (this.pieChart) this.pieChart.destroy();
      this.pieChart = new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: labels,
          datasets: [{
            label: 'Parking Spots',
            data: data,
            backgroundColor: ['rgba(16, 185, 129, 0.9)', 'rgba(239, 68, 68, 0.9)', 'rgba(245, 158, 11, 0.9)', 'rgba(139, 92, 246, 0.9)', 'rgba(6, 182, 212, 0.9)', 'rgba(236, 72, 153, 0.9)', 'rgba(37, 99, 235, 0.9)', 'rgba(156, 163, 175, 0.9)'],
            borderColor: ['rgb(16, 185, 129)', 'rgb(239, 68, 68)', 'rgb(245, 158, 11)', 'rgb(139, 92, 246)', 'rgb(6, 182, 212)', 'rgb(236, 72, 153)', 'rgb(37, 99, 235)', 'rgb(156, 163, 175)'],
            borderWidth: 4,
            hoverOffset: 20,
            hoverBorderWidth: 6
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            duration: 2500,
            easing: 'easeOutBounce'
          },
          plugins: {
            title: {
              display: true,
              text: 'Parking Spot Distribution',
              font: {
                size: 20,
                weight: 'bold'
              },
              color: '#1f2937',
              padding: 20
            },
            legend: {
              position: 'bottom',
              labels: {
                padding: 25,
                usePointStyle: true,
                pointStyle: 'circle',
                font: {
                  size: 13,
                  weight: '600'
                },
                color: '#6b7280'
              }
            },
            datalabels: {
              color: '#fff',
              formatter: (value, ctx) => {
                if (value === 0) return '';
                return value;
              },
              font: {
                weight: 'bold',
                size: 14
              }
            }
          },
          cutout: '65%'
        }
      });
    }
  }
};
